import React from 'react';
import { Document, Page } from 'react-pdf/dist/esm/entry.webpack'; // Ensure correct import path
import pdfFile from '../assets/Robert_Palermo_Resume.pdf'; // Adjusted path to your PDF file

function Resume() {
  return (
    <div className="resume">
      <h1>My Resume</h1>
      <div className="resume-content">
        <div>
          <Document file={pdfFile}>
            <Page pageNumber={1} />
          </Document>
        </div>
        {/* Add download link if needed */}
        {/* <div className="download-link">
          <a href={pdfFile} download="Robert_Palermo_Resume.pdf">
            <button>Download Resume</button>
          </a>
        </div> */}
      </div>
    </div>
  );
}

export default Resume;
